function getSubcategories(t){$(".product_subcategory_id").html("");var o="";$.get("/product_categories/getSubcategoriesCombo/"+t,function(t){for(var e in t)o=o+"<option value='"+e+"'>"+t[e]+"</option>";$(".product_subcategory_id").html(o)})}$(".product_category_id").change(function(){id=$(this).val(),getSubcategories(id)});